mapper.select:
SELECT * FROM (
    SELECT
        p.*,
        ROW_NUMBER() OVER (ORDER BY createdAt DESC) as rnum
    FROM PRODUCT p
    WHERE status = 'Y'

        AND (
            productName LIKE '%' || #{searchKeyword} || '%'
            OR description LIKE '%' || #{searchKeyword} || '%'
        )


        AND productType = #{productType}

)
WHERE rnum BETWEEN #{offset} + 1 AND #{offset} + #{limit}

SELECT COUNT(*) FROM PRODUCT WHERE status = 'Y'
AND ( productName LIKE '%' || #{searchKeyword} || '%' OR description LIKE '%' || #{searchKeyword} || '%' )
AND productType = #{productType}

mapper.update:
UPDATE PRODUCT SET productName = #{productName}, productType = #{productType}, categoryId = #{categoryId}, description = #{description}, baseRate = #{baseRate}, maturityRate = #{maturityRate}, earlyTerminateRate = #{earlyTerminateRate}, monthlyAmount = #{monthlyAmount}, savingTerm = #{savingTerm}, depositAmount = #{depositAmount}, interestMethod = #{interestMethod}, payCycle = #{payCycle},
endDate = TO_DATE(#{endDate}, 'YYYY-MM-DD'),
updatedAt = SYSDATE WHERE productNo = #{productNo}

mapper.select:
SELECT COUNT(*) FROM PRODUCT
WHERE productName = #{productName}
AND status = 'Y'
